glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D textureNoise;
void main () {
  vec4 colorMain = vec4 (1, 1, 1, 1);
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    colorMain.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    colorMain.a *= texture_tmp.a;
  #else
    colorMain *= texture_tmp;
  #endif
  vec4 colorNoise0 = texture2D (textureNoise, v_uv0);
  vec2 rel = (v_uv0 - vec2 (0.5, 0.5)) * 2.0;
  float len = sqrt (pow (rel.x, 2.0) + pow (rel.y, 2.0));
  float cosVal = rel.x / len;
  float sinVal = rel.y / len;
  colorMain.a *= pow (1.0 - abs (rel.y), 2.0);
  colorMain.a *= clamp ((1.0 - len) / 1.0, 0.0, 1.0);
  colorMain *= colorNoise0;
  colorMain *= v_color;
  gl_FragColor = colorMain.rgba;
}

