glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform eff_power_boom {
  float enabledTime;
  float loopTime;
};
void main () {
  float rateTime = clamp (mod (enabledTime, loopTime) / loopTime, 0.0, 1.0);
  rateTime = 1.0 - abs (rateTime - 0.5) / 0.5;
  vec2 rel = v_uv0 - vec2 (0.5);
  float distance = sqrt (rel.x * rel.x + rel.y * rel.y);
  float opacity = distance / 0.5;
  opacity = 1.0 - opacity;
  opacity *= step (distance, 0.5);
  float opacitint = 1.0 - opacity;
  opacitint = pow (opacitint, 2.0);
  opacitint = opacitint * 1.3;
  opacitint = clamp (opacitint, 0.0, 1.0);
  opacitint *= rateTime;
  opacity = pow (opacity, 0.5);
  opacity *= 2.0;
  opacity = clamp (opacity, 0.0, 1.0);
  vec4 colorTex = texture (texture, v_uv0);
  vec4 colorTint = vec4 (1) * (1.0 - opacitint) + v_color * opacitint;
  colorTex.a *= opacity;
  colorTex *= colorTint;
  gl_FragColor = colorTex.rgba;
}

