glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Sunshine {
  float split;
  float circle;
};
void main () {
  vec4 data = texture(texture, v_uv0);
  float angle = data[2];
  vec4 finalColor = vec4(1.0, 1.0, 1.0, 1.0);
  finalColor[3] *= abs(mod(angle + cc_time[0] / circle, (1.0 / split)) / (1.0 / split) - 0.5);
  finalColor[3] *= data[3];
  finalColor *= v_color;
  gl_FragColor = finalColor;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute vec2 a_uv0;
varying vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
uniform vec4 cc_time;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
varying vec2 v_uv0;
uniform sampler2D texture;
uniform float split;
uniform float circle;
void main () {
  vec4 data = texture2D(texture, v_uv0);
  float angle = data[2];
  vec4 finalColor = vec4(1.0, 1.0, 1.0, 1.0);
  finalColor[3] *= abs(mod(angle + cc_time[0] / circle, (1.0 / split)) / (1.0 / split) - 0.5);
  finalColor[3] *= data[3];
  finalColor *= v_color;
  gl_FragColor = finalColor;
}
