glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform eff_circle_angulation_add_mul {
  float offset;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  vec2 rel = (v_uv0 - vec2 (0.5, 0.5)) * 2.0;
  float len = sqrt (pow (rel.x, 2.0) + pow (rel.y, 2.0));
  float cos = rel.x / len;
  float sin = rel.y / len;
  float rate = o.a * clamp (1.4142 - len, 0.0, 1.0);
  rate = sqrt (rate);
  vec4 c = vec4 (- cos * offset / 255.0 * rate, - sin * offset / 255.0 * rate, 0, 1);
  c *= v_color;
  gl_FragColor = vec4 (c.rgb, abs (clamp (c.r, 0.0, 1.0)) + abs (clamp (c.g, 0.0, 1.0)));
}

