glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform eff_bring {
  float enabledTime;
};
float atan2 (float y, float x) {
  return atan (y / x) + step (x, 0.0) * step (0.0, y) * 3.1415 - step (x, 0.0) * step (y, 0.0) * 3.1415;
}
void main () {
  float msFadeIn = 20.0;
  float msFadeOut = 600.0;
  float radiusBoomStart = 0.8;
  float radiusBoomEnd = 0.2;
  float lineStart = sqrt (2.0 * pow (0.5, 2.0));
  float lineEnd = lineStart;
  float rateIn = clamp ((enabledTime - msFadeIn) / msFadeIn, 0.0, 1.0);
  float rateOut = clamp ((msFadeOut - enabledTime) / msFadeOut, 0.0, 1.0);
  rateOut = pow (rateOut, 4.0);
  float radiusBoom = radiusBoomStart + (radiusBoomEnd - radiusBoomStart) * (1.0 - rateOut);
  float radiusLine = lineStart + (lineEnd - lineStart) * (1.0 - rateOut);
  vec2 rel = v_uv0 - vec2 (0.5);
  float distance = sqrt (dot (rel, rel));
  float angle = atan2 (rel.y, rel.x) + 3.1415;
  float x = (mod (angle, 3.1415 / 2.0)) / (3.1415 / 2.0);
  float y = 1.0 - abs (x - 0.5) / 0.5;
  y = pow (y, 2.0);
  y *= radiusLine;
  y += step (y, radiusBoom) * (radiusBoom - y) * 0.5;
  float opacityStar = clamp ((y - distance) / distance, 0.0, 1.0);
  opacityStar = pow (opacityStar, 2.0);
  vec4 o = vec4(1, 1, 1, 1);
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  o.a *= opacityStar;
  o.a *= rateIn;
  o.a *= rateOut;
  o *= v_color;
  gl_FragColor = o.rgba;
}

