glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform EffSmoothPro {
  float textureWidth;
  float textureHeight;
  float additionTintR;
  float additionTintG;
  float additionTintB;
  float additionTintA;
};
void main () {
  vec4 colorTex = texture2D (texture, v_uv0);
  colorTex.rgb += (vec3 (1) - colorTex.rgb) * vec3 (additionTintR, additionTintG, additionTintB) * additionTintA;
  gl_FragColor = colorTex * v_color;
}

