precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
varying vec2 v_uv0;
uniform sampler2D texture;
uniform vec4 tintStarColor;
uniform vec4 tintEndColor;
uniform float tintStar;
uniform float tintEnd;
void main () {
  vec4 colorTint = vec4 (0.0, 0.0, 0.0, 0.0);
  colorTint += step (v_uv0.y, tintStar) * tintStarColor;
  float rate = (v_uv0.y - tintStar) / (tintEnd - tintStar);
  colorTint += step (tintStar, v_uv0.y) * step (v_uv0.y, tintEnd) * ((1.0 - rate) * tintStarColor + rate * tintEndColor);
  colorTint += step (tintEnd, v_uv0.y) * tintEndColor;
  vec4 o = vec4(1, 1, 1, 1);
  vec4 texture_tmp = texture2D(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
